import React, { useState } from 'react'
import { useHistory } from 'react-router'

const useFormField = () => {
    const [value, setValue] = useState('')
    return [value, e => setValue(e.target.value)]
}

const HostSignup = () => {
    const [name, setName] = useFormField()
    const [surname, setSurname] = useFormField()
    const [email, setEmail] = useFormField()
    const [password, setPassword] = useFormField()
    const [foo, setFoo] = useState('foo')
    const handleFoo = e => setFoo(e.target.value)
    const [legal, setLegal] = useState(false)
    const handleLegal = e => setLegal(e.target.checked)


    const history = useHistory()
    const [isError, setError] = useState(false)

    const handleSubmit = async (e) => {
        e.preventDefault()
        const user = { name, surname, email, password, colivingName, street }
        setError(false)
        try {
            const ret = await fetch('http://localhost:3300/signup', {
                method: 'POST',
                body: JSON.stringify(user),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const data = await ret.json()  //aqui error --> unexpected end of json at handlesubmit
            history.push(`/users/${data.id}`)
        } catch (err) {
            console.warn('Error:', err)
            setError(true)
        }
    }

    return (
        <form id="signup" onSubmit={handleSubmit}>
            <label>
                Información de contacto
                <input name="name" required value={name} onChange={setName} placeholder="Nombre" />
                <input name="surname" required value={surname} onChange={setSurname} placeholder="Apellido" />
                <input name="email" type="email" required value={email} onChange={setEmail} placeholder="Email"/>
                <input name="password" type="password" required value={password} onChange={setPassword} placeholder="Contraseña"/>
            </label>
            <label>
                Información del coliving
                <input name="colivingName" required value={colivingName} onChange={setName} placeholder="Nombre" />
                <label>
                    Dirección:
                    <input name="street" required value={street} onChange={setStreet} placeholder="Calle" />
                    <input name="email" type="email" required value={email} onChange={setEmail} placeholder="Email"/>
                    <input name="password" type="password" required value={password} onChange={setPassword} placeholder="Contraseña"/>  
                </label>


                <select value={foo} onChange={handleFoo}>
                    <option value="foo">Foo</option>
                    <option value="bar">Bar</option>
                    <option value="baz">Baz</option>
                </select>
                <label>
                    <input type="checkbox" checked={legal} onChange={handleLegal} />
                    Acepto terminos y condiciones
                </label>

            </label>

            <button>Registrarse</button>
            {isError && <div>Error, inténtalo de nuevo</div>}
        </form>
    )
}

export default HostSignup
