import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router'
import Address from './Address'
import Radios from './Radios'


const useFormField = () => {
    const [value, setValue] = useState('')
    return [value, e => setValue(e.target.value)]
}

const HostSignup = () => {
    const dispatch = useDispatch()
    const login = (user) => dispatch({ type: 'login', user })
    const [name, setName] = useFormField()
    const [surname, setSurname] = useFormField()
    const [email, setEmail] = useFormField()
    const [password, setPassword] = useFormField()
    const [colivingName, setColivingName] = useFormField()
    const [address, setAddress] = useState({})
    const [photo, setPhoto] = useFormField({})
    const [apartments, setApartments] = useFormField()
    const [rooms, setRooms] = useFormField()
    const [roomType, handleRoomType] = useFormField()
    const [facilities, setFacilities] = useState('facilities')
    const handleFacilities = e => setFacilities(e.target.value)
    const [website, setWebsite] = useFormField()
    const [facebook, setFacebook] = useFormField()
    const [instagram, setInstagram] = useFormField()
    const [twitter, setTwitter] = useFormField()
    const [legal, setLegal] = useState(false)
    const handleLegal = e => setLegal(e.target.checked)


    const history = useHistory()
    const [isError, setError] = useState(false)

/*const isLoggedIn = useSelector(s => !!s.user)
    useEffect(() => {
        if (isLoggedIn) {
            history.push(`/host/dashboard`)
        }
    }, [isLoggedIn])
*/

    const handleSubmit = async (e) => {
        e.preventDefault()
        const coliving = { name, surname, email, password, colivingName, address, apartments, rooms, roomType, facilities, website, facebook, instagram, twitter }
        setError(false)
        try {
            const ret = await fetch('http://localhost:3300/host/signup', {
                method: 'POST',
                body: JSON.stringify(coliving),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const data = await ret.json()  
            if (ret.ok)
                login(data)
        } catch (err) {
            console.warn('Error:', err)
            setError(true)
        }
    }

    return (
        <form id="signup" onSubmit={handleSubmit}>
            <section id="contact-info">
            <label>
                <h3>Información de contacto</h3>
                <input name="name" required value={name} onChange={setName} placeholder="Nombre" />
                <input name="surname" required value={surname} onChange={setSurname} placeholder="Apellido" />
                <input name="email" type="email" required value={email} onChange={setEmail} placeholder="Email" />
                <input name="password" type="password" required value={password} onChange={setPassword} placeholder="Contraseña" />
            </label>
            </section>

            <section id="coliving-info">
            <label>
                <h3>Información del coliving</h3>
                <input name="colivingName" required value={colivingName} onChange={setColivingName} placeholder="Nombre del espacio" />
                <Address value={address} onChange={setAddress} />
                <input name="photo" value={photo} onChange={setPhoto} placeholder="url imagen" />
                <label>
                    <p>Nº de apartamentos</p>
                <input type="number" name="apartments" min="1" max="25" required value={apartments} onChange={setApartments} />
                </label>
                <label>
                    <p>Nº total de habitaciones</p>
                <input type="number" name="rooms" min="5" max="100" required value={rooms} onChange={setRooms} />
                </label>
                <Radios name="roomType" options={["compartidas", "privadas", "ambas"]} value={roomType} onChange={handleRoomType} />
                <div id="services">
                    <p>Servicios incluidos</p>
                <select name="facilities" value={[facilities]}  multiple size="1" onChange={handleFacilities} >
                    <optgroup label="Instalaciones y servicios">
                        <option value="wifi">Wifi</option>
                        <option value="suplies">Suministros incluidos</option>
                        <option value="cleaning">Limpieza incluida</option>
                        <option value="cafeteria">Cafetería</option>
                        <option value="laundry">Lavanderia</option>
                        <option value="coworking">Coworking</option>
                        <option value="gym">Gimnasio</option>
                        <option value="garden">Jardín</option>
                    </optgroup>
                </select>
                </div>
                <input type="url" name="website" value={website} onChange={setWebsite} placeholder="Sitio web" />
                <input type="url" name="facebook" value={facebook} onChange={setFacebook} placeholder="Link Facebook" />
                <input type="url" name="instagram" value={instagram} onChange={setInstagram} placeholder="Link Instagram" />
                <input type="url" name="twitter" value={twitter} onChange={setTwitter} placeholder="Link Twitter" />                  
            </label>
            </section>

            <label>
                <input type="checkbox" checked={legal} onChange={handleLegal} />
                <p>Acepto terminos y condiciones</p>
            </label>

            <button>Registrarse</button>
            {isError && <div>Error, inténtalo de nuevo</div>}
        </form>
    )
}

export default HostSignup
