import React, { useState } from 'react'
import { useHistory } from 'react-router-dom'

const useFormField = () => {
    const [value, setValue] = useState('')
    return [value, e => setValue(e.target.value)]
}

const Login = () => {
    const [email, setEmail] = useFormField()
    const [password, setPassword] = useFormField()

    const history = useHistory()
    const [isError, setError] = useState(false)

    const handleSubmit = async (e) => {
        e.preventDefault()
        console.log(user)
        const user = { email, password }
        setError(false)
        try {
            const ret = await fetch('http://localhost:3300/login', {
                method: 'POST',
                body: JSON.stringify(user),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            console.log(data)
            const data = await ret.json()
            localStorage.setItem('token', data.token)
            history.push(`/users/${data.id}`)
        } catch (err) {
            console.warn('Error:', err)
            setError(true)
        }
    }

    return (
        <form id="signup" onSubmit={handleSubmit}>
            <label>
                Email:
                <input name="email" type="email" required value={email} onChange={setEmail} />
            </label>
            <label>
                Contraseña:
                <input name="password" type="password" required value={password} onChange={setPassword} />
            </label>
            <button>"Login"</button>
            {isError && <div>Error, email o contraseña incorrectos</div>}
        </form>
    )
}

export default Login
