import React, { useState } from "react";


const useFormField = (InitialValue) => {
    const [ value, setValue ] = (InitialValue || '')
    const handleChange = e => setValue(e.target.value)
    return [ value, handleChange ]
}

const Search = () => {
    const [ searchCity, handleSearchCity ] = useFormField('');
    const [ searchEntryWeek, handleSearchEntryWeek ] = useFormField('');
    const [ searchDepartureWeek, handleSearchDepartureWeek ] = useFormField('');
    const [ searchPrize, handleSearchPrize ] = useFormField('');
    const [ searchProfession, handleSearchProfession ] = useFormField('');
    const [ status, setStatus ] = useState ()

    const handleSubmit = e => {
        e.preventDefault()
        setStatus('loading')
    }

    if (status === 'loading') {
        return (
            <p>Buscando...</p>
        )
    }

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" value={searchCity} onChange={handleSearchCity} placeholder="Ciudad" />
            <input type="date" value={searchEntryWeek} onChange={handleSearchEntryWeek} placeholder="Entrada" />
            <input type="date" value={searchDepartureWeek} onChange={handleSearchDepartureWeek} placeholder="Salida" />
            <input type="number" min="150" max="1000" value={searchPrize} onChange={handleSearchPrize} placeholder="precio/semana" />
            <input type="text" value={searchProfession} onChange={handleSearchProfession} placeholder="profesion afÃ­n" />
            <button>Enviar</button>
            {status}
        </form>
    );
}

export default Search;
