import React, { useState, useEffect } from 'react'
import Search from './Search';
import Coliving from './Coliving'

const Colivings = () => {
    const [loading, setLoading] = useState(true)
    const [colivings, setColivings] = useState([])
    const [errorMessage, setErrorMessage] = useState(null);
    useEffect(() => {
        fetch('http://localhost:3300/colivings')
            .then(response => response.json())
            .then(jsonResponse => {
                setColivings(jsonResponse.Search);
                setLoading(false);
            });
    }, []);

    //cambiar: filter al array de todos los colivings. Evitar 2ª consulta a bbdd
    const search = (searchValue) => {
        setLoading(true);
        setErrorMessage(null);
        fetch('http://localhost:3300/colivings', {
            method: 'POST',
            body: JSON.stringify(searchValue),
            headers: {
                'Content-Type': 'application/json'
            }
                .then(response => response.json())
                .then(jsonResponse => {
                    if (jsonResponse.Response === "True") {
                        setColivings(jsonResponse.Search);
                        console.log(jsonResponse)
                        setLoading(false);
                    } else {
                        setErrorMessage(jsonResponse.Error);
                        setLoading(false);
                    }
                })
        });
    }
    return (
        <div className="colivingssearch">
            <h1>Welcome! Find your next home:</h1>
            <Search search={search} />
            <div className="colivings">
                {loading && !errorMessage ? (
                    <span>loading...</span>
                ) : errorMessage ? (
                    <div className="errorMessage">{errorMessage}</div>
                ) : (
                            colivings.map((coliving, index) => (
                                <Coliving key={`${index}-${coliving.id}`} coliving={coliving.name} />
                            ))
                        )}
            </div>
        </div>
    );
};

export default Colivings
