import React, { useState, useEffect } from 'react'
import { useHistory, useParams } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'

const EditProfile = () => {
    const { id } = useParams()
    const editprofile = (user) => dispatch({ type: 'editprofile', user })
    const dispatch = useDispatch()
    const user = useSelector(s => s.user)
    const handler = field => e => ({ ...user, [field]: e.target.value })
    useEffect(() => {
        fetch('http://localhost:3300/users/' + id)
            .then(r => r.json())
            .then(data => setUser(data))
    }, [id])

    const history = useHistory()
    const [isError, setError] = useState(false)

    const handleSubmit = async (e) => {
        e.preventDefault()
        setError(false)
        try {
            const ret = await fetch('http://localhost:3300/users/' + id, {
                method: 'PUT',
                body: JSON.stringify(user),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            await ret.json()
            history.push(`/users/${id}`)
        } catch (err) {
            console.warn('Error:', err)
            setError(true)
        }
    }

    if (!user) return 'Loading...'

    return (
        <form id="signup" onSubmit={handleSubmit}>
            <label>
                Nombre:
                <input name="name" required value={user.name} onChange={handler('name')} />
            </label>
            <label>
                Apellido:
                <input name="surname" required value={user.surname} onChange={handler('surname')} />
            </label>
            <label>
                Email:
                <input name="email" required value={user.email} onChange={handler('email')} />
            </label>
            <label>
                Profesión:
                <input name="profession" required value={user.profession} onChange={handler('profession')} />
            </label>
            <label>
                País:
                <textarea name="country" required value={user.country} onChange={handler('country')} />
            </label>
            <button>Guardar</button>
            {isError && <div>Error, please try again</div>}
        </form>
    )
}

export default EditProfile
