import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux'
import Radios from '../Radios'

const useFormField = () => {
    const [value, setValue] = useState('')
    return [value, e => setValue(e.target.value)]
}

const AddApartment = () => {
    const user = useSelector(s => s.user)
    const coliving = useSelector(s => s.coliving)
    const [rooms, setRooms] = useFormField()
    const [roomType, handleRoomType] = useFormField()
    const [photo, setPhoto] = useFormField({})

    if (!user) return 'Loading...';
    const [isError, setError] = useState(false)

    const handleSubmit = async (e) => {
        e.preventDefault()
        const appartment = { rooms, roomType, photo, coliving }
        setError(false)
        try {
            const ret = await fetch('http://localhost:3300/host/add/appartment', {
                method: 'POST',
                body: JSON.stringify(appartment),
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + token
                }
            })
            const data = await ret.json()
            if (ret.ok)
                res.send(data)
        } catch (err) {
            console.warn('Error:', err)
            setError(true)
        }
    }

    return (
        <form id="add-apartment" onSubmit={handleSubmit}>
            <h2>Añadir apartamento</h2>
            <div>
                <label>
                    Nº de habitaciones:
                    <input type="number" name="rooms" min="1" max="10" required value={rooms} onChange={setRooms} />
                </label>
            </div>
            <div>
                <label>
                    Tipo de habitaciones:
                    <Radios name="roomType" options={["compartidas", "privadas", "ambas"]} value={roomType} onChange={handleRoomType} />
                </label>
            </div>
            <div>
                <label>
                    Foto:
                    <input name="photo" value={photo} onChange={setPhoto} placeholder="url imagen" />
                </label>
            </div>         
            <button>Añadir</button>
            {isError && <div>Error, inténtalo de nuevo</div>}
        </form>
    )
}

export default AddApartment
