import React, { useState } from "react";

const useFormField = (valorInicial) => {
    const [value, setValue] = useState(valorInicial || '')
    const handleChange = e => setValue(e.target.value)
    return [value, handleChange]
  }

const Reservations = () => {
    const [email, handleEmail] = useFormField('');
    const [password, handlePassword] = useFormField('');
    const [EntryWeek, handleEntryWeek] = useFormField('');
    const [DepartureWeek, handleDepartureWeek] = useFormField('');
    const [status, setStatus] = useState()

    const handleSubmit = async e => {
        e.preventDefault()
        const reservation ={ email, password, entryWeek, departureWeek }
        setStatus('loading...')
        await fetch('http://localhost:3300/reservations', {
          method: 'POST',
          body: JSON.stringify(reservation),
          headers: {
            'Content-Type': 'application/json'
        }
        })
        setStatus('ok!')
      } 


return (
    <form className="bookings" onsubmit={handleSubmit}>
        <div className="client-email">
                <label className="guest-email">
                    Email
                </label>
                <input className="email-booking" type="email" value={email} onChange={handleEmail} />
            </div>
            <div className="client-password">
                <label className="guest-password">
                    Contrase√±a
                </label>
                <input className="password-booking" type="password" value={password} onChange={handlePassword} />
            </div>
            <div className="entry-booking">
                <label className="client-entry">
                    Entrada
                </label>
                <input className="entry-booking" type="date" value={EntryWeek} onChange={handleEntryWeek} />
            </div>
            <div className="departure-booking">
                <label className="client-departure">
                    Salida
                </label>
                <input className="departure-booking" type="date" value={DepartureWeek} onChange={handleDepartureWeek} />
            </div>
            <button className="make-reservation">Reservar</button>
      {status}


    </form>
)
}

export default Reservations;
