import React, { useState, useEffect } from 'react'
import { useHistory, useParams } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'

const useEditProfile = () => {
    const { id } = useParams()
    const dispatch = useDispatch()
    const token = useSelector(s => s.user && s.user.token)
    const user = useSelector(s => s.user)

    useEffect(() => {

        const setUser = user => dispatch({ type: 'editprofile', user })        
        fetch('http://localhost:3300/users/' + id), {
            headers: {
                'Authorization': 'Bearer ' + token
            }
                .then(r => r.json())
                .then(data => setUser(data))
        }, [id, token, dispatch]
    return user
})




const EditProfile = () => {
    const handler = field => e => setUser({ ...user, [field]: e.target.value })
    const editProfile = useEditProfile()
    const history = useHistory()
    const [isError, setError] = useState(false)

const handleSubmit = async (e) => {
    e.preventDefault()
    setError(false)
    try {
        const ret = await fetch('http://localhost:3300/users/' + id, {
            method: 'PUT',
            body: JSON.stringify(user),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        await ret.json()
        history.push(`/users/${id}`)
    } catch (err) {
        console.warn('Error:', err)
        setError(true)
    }
}
}
    if (!user) return 'Loading ...'
    return (
        <form id="signup" onSubmit={handleSubmit}>
            <label>
                Nombre:
                <input name="name" required value={user.name} onChange={handler('name')} />
            </label>
            <label>
                Apellido:
                <input name="surname" required value={user.surname} onChange={handler('surname')} />
            </label>
            <label>
                Email:
                <input name="email" required value={user.email} onChange={handler('email')} />
            </label>
            <label>
                Profesión:
                <input name="profession" required value={user.profession} onChange={handler('profession')} />
            </label>
            <label>
                País:
                <textarea name="country" required value={user.country} onChange={handler('country')} />
            </label>
            <button>Guardar</button>
            {isError && <div>Error, please try again</div>}
        </form>
    )
}

export default EditProfile
