import React, { useState } from 'react'
import { useHistory } from 'react-router-dom'


const useFormField = () => {
    const [value, setValue] = useState('')
    return [value, e => setValue(e.target.value)]
}

const Signup = () => {
    const [name, setName] = useFormField()
    const [surname, setSurname] = useFormField()
    const [email, setEmail] = useFormField()
    const [password, setPassword] = useFormField()

    const history = useHistory()
    const [isError, setError] = useState(false)

    const handleSubmit = async (e) => {
        e.preventDefault()
        const user = { name, surname, email, password }
        //console.log(user)
        setError(false)
        try {
            const ret = await fetch('http://localhost:3300/signup', {
                method: 'POST',
                body: JSON.stringify(user),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const data = await ret.json()
            //console.log(data)
            history.push(`/users/${data.id}`)
        } catch (err) {
            console.warn('Error:', err)
            setError(true)
        }
    }

    return (
        <form id="signup" onSubmit={handleSubmit}>
            <label>
                Name:
                <input name="name" required value={name} onChange={setName} />
            </label>
            <label>
                Apellidos:
                <input name="surname" required value={surname} onChange={setSurname} />
            </label>
            <label>
                Email:
                <input name="email" type="email" required value={email} onChange={setEmail} />
            </label>
            <label>
                Contraseña:
                <input name="password" type="password" required value={password} onChange={setPassword} />
            </label>
            <button>"Registrarse"</button>
            {isError && <div>Error, inténtalo de nuevo</div>}
        </form>
    )
}

export default Signup
