import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import './Login.css'

const useFormField = () => {
    const [value, setValue] = useState('')
    return [value, e => setValue(e.target.value)]
}

const Login = () => {
    const dispatch = useDispatch()
    const login = (user) => dispatch({ type: 'login', user })
    const [email, setEmail] = useFormField()
    const [password, setPassword] = useFormField()

    const history = useHistory()
    const [isError, setError] = useState(false)

    const isLoggedIn = useSelector(s => !!s.user)
    useEffect(() => {
        if (isLoggedIn) {
            history.push(`/user`)
        }
    }, [isLoggedIn])


    const handleSubmit = async (e) => {
        e.preventDefault()
        const user = { email, password }
        setError(false)
        try {
            const ret = await fetch('http://localhost:3300/login', {
                method: 'POST',
                body: JSON.stringify(user),
                headers: {
                    'Content-Type': 'application/json'
                }
            })

            const data = await ret.json()
            if (ret.ok)
                login(data)
            // history.push(`/user/${data.id}`)
        } catch (err) {
            console.warn('Error:', err)
            setError(true)
        }
    }

    return (
        <form id="login" onSubmit={handleSubmit}>
            <input name="email" type="email" required value={email} onChange={setEmail} placeholder="Email"/>
            <input name="password" type="password" required value={password} onChange={setPassword} placeholder="Password"/>
            <button>Login</button>
            {isError && <div>Error, email o contrase√±a incorrectos</div>}
        </form>
    )
}

export default Login
