const rootReducer = (state, action) => {
    switch (action.type) {
        case 'login':
            return { ...state, user: action.user }
        case 'logout':
            return { ...state, user: null }
        case 'products':
            return { ...state, products: action.products }      
        default:
            return state || {
                tema: 'dark',
                visits: 0
            }
    }
}

export default rootReducer
