import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'

const useFormField = () => {
    const [value, setValue] = useState('')
    return [value, e => setValue(e.target.value)]
}

const Signup = () => {
    const dispatch = useDispatch()
    const login = (user) => dispatch({ type: 'login', user })
    const [name, setName] = useFormField()
    const [surname, setSurname] = useFormField()
    const [email, setEmail] = useFormField()
    const [password, setPassword] = useFormField()
    const [profession, setProfession] = useFormField()
    const [country, setCountry] = useFormField()

    const [isError, setError] = useState(false)

    const isLoggedIn = useSelector(s => !!s.user)
    useEffect(() => {
        if (isLoggedIn) {
            history.push(`/user`)
        }
    }, [isLoggedIn])

    const handleSubmit = async (e) => {
        e.preventDefault()
        const user = { name, surname, email, password, profession, country }
        setError(false)
        try {
            const ret = await fetch('http://localhost:3300/signup', {
                method: 'POST',
                body: JSON.stringify(user),
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const data = await ret.json()
            login(data)
            //history.push(`/user/${data.id}`)
        } catch (err) {
            console.warn('Error:', err)
            setError(true)
        }
    }

    return (
        <form id="signup" onSubmit={handleSubmit}>
            <input name="name" required value={name} onChange={setName} placeholder="Nombre" />
            <input name="surname" required value={surname} onChange={setSurname} placeholder="Apellido"/>
            <input name="email" type="email" required value={email} onChange={setEmail} placeholder="Email"/>
            <input name="password" type="password" required value={password} onChange={setPassword} placeholder="Contraseña"/>
            <input name="profession" required value={profession} onChange={setProfession} placeholder="Profesión" />
            <input name="country" required value={country} onChange={setCountry} placeholder="País" />
            <button>Registrarse</button>
            {isError && <div>Error, inténtalo de nuevo</div>}
        </form>
    )
}

export default Signup
