import React, { useState } from "react";


const useFormField = (initialValue) => {
    const [searchValue, setSearchValue] = useState(initialValue || '')
    const handleChange = e => setSearchValue(e.target.value)
    return [searchValue, handleChange]
}

const Search = () => {
    const [searchCity, handleSearchCity] = useFormField('');
    const [searchEntryWeek, handleSearchEntryWeek] = useFormField('');
    const [searchDepartureWeek, handleSearchDepartureWeek] = useFormField('');
    const [searchPrize, handleSearchPrize] = useFormField('');
    const [searchProfession, handleSearchProfession] = useFormField('');
    const [status, setStatus] = useState()

    const handleSubmit = e => {
        e.preventDefault()
        setStatus('loading')
    }


    if (status === 'loading') {
        return (
            <p>Buscando...</p>
        )
    }

    return (
        <form className="search" onSubmit={handleSubmit}>
            <div className="city-filter">
                <label class="city">
                    Ciudad
                </label>
                <input class="searchcity" type="text" value={searchCity} onChange={handleSearchCity} />
            </div>
            <div className="prize-filter">
                <label class="prize">
                    Precio/semana
                </label>
                <input class="searchprize" type="number" min="150" max="1000" value={searchPrize} onChange={handleSearchPrize} />
            </div>
            <div className="entry-filter">
                <label class="entry">
                    Fecha de entrada
                </label>
                <input class="searchentry" type="date" value={searchEntryWeek} onChange={handleSearchEntryWeek} />
            </div>
            <div className="departure-filter">
                <label class="departure">
                    Fecha de salida
                </label>
                <input class="searchdeparture" type="date" value={searchDepartureWeek} onChange={handleSearchDepartureWeek} />
            </div>
            <div className="profession-filter">
            <label class="profession">
                Profesión afín
            </label>           
                <input class="searchprofession" type="text" value={searchProfession} onChange={handleSearchProfession} />
            </div>
            <button>Buscar</button>
            {status}
        </form>
    );
}

export default Search;
