import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'

const getColiving = id =>
    fetch(`http://localhost:3300/colivings/${id}`)
        .then(r => r.json())

const ColivingInfo = () => {
    const { id } = useParams()

    const [coliving, setColiving] = useState()
    useEffect(() => {
        getColiving(id).then(c => setColiving(c))
    }, [id])
    if (!coliving) {
        return (
            "loading"
        )
    }

    const divInfoStyle = {
        backgroundImage: `url(${coliving.poster})`,
        backgroundPosition: "center",
        backgroundSize: "cover",
        width: "50rem",
        height: "30.2rem",
        opacity: 0.85
      }


    return (
        <div className="coliving-info">
            <div className="coliving-pic">
                <div className="coliving-info-img" style={divInfoStyle} alt={`${coliving.name} en ${coliving.city}`}></div>
            </div>
            <h2>{coliving.name}</h2>
            <div className="details">
                <span className="info-location">{coliving.city}</span>
                <br />
                <span className="info-prize">{coliving.prize}</span>
                <br />
                <span className="info-punctuation">{coliving.punctuation}</span>
            </div>
            <p className="more">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec gravida diam id commodo volutpat. Sed nulla eros, rutrum eget ante ut, commodo ullamcorper nisi. Praesent non felis erat. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi auctor erat et purus commodo fermentum. Maecenas mattis massa ac mollis venenatis. Sed in sem quam. Mauris luctus fringilla mi in iaculis. Pellentesque orci ex, consectetur non euismod vitae, pretium at nunc. Maecenas id dui egestas, suscipit nisi vel, venenatis massa. Nam varius volutpat tincidunt. Quisque feugiat malesuada magna in molestie</p>
        </div>
    )
}

export default ColivingInfo
