import React, { useState } from "react";
import './Search.css';


const useFormField = (initialValue) => {
    const [searchValue, setSearchValue] = useState(initialValue || '')
    const handleChange = e => setSearchValue(e.target.value)
    return [searchValue, handleChange]
}

const Search = ( {search}) => {
    const [searchCity, handleSearchCity] = useFormField('');
    const [searchEntryWeek, handleSearchEntryWeek] = useFormField('');
    const [searchDepartureWeek, handleSearchDepartureWeek] = useFormField('');
    const [searchPrize, handleSearchPrize] = useFormField('');
    const [searchMonthPrize, handleSearchMonthPrize] = useFormField('');
    const [searchProfession, handleSearchProfession] = useFormField('');
    const [status, setStatus] = useState()

    const handleSubmit = e => {
        e.preventDefault()
        setStatus('loading')
        search({ city: searchCity })
    }


    if (status === 'loading') {
        return (
            <p>Buscando...</p>
        )
    }

    return (
        <form className="search" onSubmit={handleSubmit}>
            <div className="city-filter">
                <label className="city">
                    Ciudad
                </label>
                <input className="searchcity" type="text" value={searchCity} onChange={handleSearchCity} />
            </div>
            <div className="entry-filter">
                <label className="entry">
                    Entrada
                </label>
                <input className="searchentry" type="date" value={searchEntryWeek} onChange={handleSearchEntryWeek} />
            </div>
            <div className="departure-filter">
                <label className="departure">
                    Salida
                </label>
                <input className="searchdeparture" type="date" value={searchDepartureWeek} onChange={handleSearchDepartureWeek} />
            </div>
            <div className="prize-filter">
                <label className="prize">
                    Precio/semana
                </label>
                <input className="searchprize" type="number" min="150" max="1000" value={searchPrize} onChange={handleSearchPrize} />
            </div>
            <div className="prize-month-filter">
                <label className="month-prize">
                    Precio/mes
                </label>
                <input className="search-month-prize" type="number" min="600" max="3000" value={searchMonthPrize} onChange={handleSearchMonthPrize} />
            </div>
            <div className="profession-filter">
                <label className="profession">
                    Profesión
                </label>
                <input className="searchprofession" type="text" value={searchProfession} onChange={handleSearchProfession} />
            </div>
            <button className="filter">Buscar</button>
            {status}
        </form>
    );
}

export default Search;
